import { useState } from "react";
const BasicForm = (props) => {
  const [formData, setFormData] = useState({
    fname: '',
    lname: '',
    email: ''
  })
  const [formError, setFormError] = useState({
    fnameError: "",
    lnameError: '',
    emailError: ''
  })

  const chageFormDateHandler = (event) => {
    const {name, value} = event.target
    setFormData((prevState) => ({...prevState, [name]: value}))
  }

  const formSubmitHandler = (event) => {
    event.preventDefault();

    console.log(formData)
  }
  const checkValidationHandler = () => {
    if(formData.fname.trim() === ''){
      setFormError((prevState) => ({...prevState, fnameError: 'First name can\'t be empty'})) 
    }
    if(formData.lname.trim() === ''){
      setFormError((prevState) => ({...prevState, lnameError: 'Last name can\'t be empty'})) 
    }
    if(!formData.email.includes('@')){
      setFormData((prevState)=> ({...prevState, emailError: "Email is not valid"}))
    }
  }

  return (
    <form onSubmit={formSubmitHandler}>
      <div className='control-group'>
        <div className='form-control'>
          <label htmlFor='name'>First Name</label>
          <input type='text' 
          id='name' 
          name = "fname" 
          onChange={chageFormDateHandler} 
          onBlur = {checkValidationHandler}
          />
          {formError.fnameError && <p>{formError.fnameError}</p>}
        </div>
        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' name= "lname" onChange={chageFormDateHandler} />
          {formError.lnameError && <p>{formError.lnameError}</p>}
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' name= "email" onChange={chageFormDateHandler}/>
        {formError.emailError && <p>{formError.emailError}</p>}
      </div>
      <div className='form-actions'>
        <button type="submit">Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
