import { useEffect, useState } from "react";
import useInput from "../hooks/use-input";
const BasicForm = (props) => {

  const {value: formData , valueTouched: formDataTouched, valueChangeHandler : formChangeHandler, 
    valueTouchedChangeHandler: formTouchedChangeHandler } = useInput();
  const formError = {fnameError: '', lnameError: '', emailError: ''}

  return (
    <form onSubmit={formSubmitHandler}>
      <div className='control-group'>
        <div className='form-control'>
          <label htmlFor='name'>First Name</label>
          <input type='text' 
          id='name' 
          name = "fname" 
          onChange={formChangeHandler} 
          onBlur = {formTouchedChangeHandler}
          />
        </div>
        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' 
          id='name' 
          name= "lname" 
          onChange={formChangeHandler}  
          onBlur = {formTouchedChangeHandler}/>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text'
         id='name' 
         name= "email"
        onChange={formChangeHandler} 
        onBlur = {formTouchedChangeHandler}/>
      </div>
      <div className='form-actions'>
        <button type="submit">Submit</button>
      </div>
      </div>
    </form>
  );
};

export default BasicForm;
