import useInput from "../hooks/use-input";

const BasicForm = () => {
  const {value: formData ,
     valueTouched: formDataTouched, 
     valueChangeHandler : formChangeHandler, 
    valueTouchedChangeHandler: formTouchedChangeHandler,
    isValid} = useInput({
      fname: (text)=> text?.trim() !== ""
    });

    const formSubmitHandler = (event) => {
      event.preventDefault();
      console.log(formData, formDataTouched, isValid)
    }

  return<form onSubmit={formSubmitHandler}>
      <div className='control-group'>
        <div className='form-control'>
          <label htmlFor='name'>First Name</label>
          <input type='text' 
          id='name' 
          name = "fname" 
          onChange={formChangeHandler} 
          onBlur = {formTouchedChangeHandler}
          />
          {isValid?.name && <p>First name can't be empty</p>}
        </div>
        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' 
          id='name' 
          name= "lname" 
          onChange={formChangeHandler}  
          onBlur = {formTouchedChangeHandler}/>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text'
         id='name' 
         name= "email"
        onChange={formChangeHandler} 
        onBlur = {formTouchedChangeHandler}/>
      </div>
      <div className='form-actions'>
        <button type="submit">Submit</button>
      </div>
      </div>
    </form>

};

export default BasicForm;
