import { useEffect, useState } from "react";
import useInput from "../hooks/use-input";
const BasicForm = (props) => {

  const {value: formData , valueTouched: formDataTouched, valueChangeHandler : formChangeHandler, 
    valueTouchedChangeHandler: formTouchedChangeHandler } = useInput();
  const formError = {fnameError: '', lnameError: '', emailError: ''}

  const chageFormDateHandler = (event) => {
    const {name, value} = event.target
    if(name === 'fname'){
      setFormDataTouched((prevState) => ({...prevState, fnameTouched: true}))
    }
    if(name === 'lname'){
      setFormDataTouched((prevState) => ({...prevState, lnameTouched: true}))
    }
    if(name === 'email'){
      setFormDataTouched((prevState) => ({...prevState, emailTouched: true}))
    }
    setFormData((prevState) => ({...prevState, [name]: value}))
  }

  useEffect(()=> {
    if(formData.fname?.trim() !== ''){
      setFormError((prevState) => ({...prevState, fnameError: ''})) 
    }
    if(formData.lname?.trim() !== ''){
      setFormError((prevState) => ({...prevState, lnameError: ''})) 
    }
    if(formData.email?.includes('@')){
      setFormError((prevState)=> ({...prevState, emailError: ""}))
    }
  }, [formData.fname, formData.lname, formData.email])
  const formSubmitHandler = (event) => {
    event.preventDefault();
    console.log(formData)
  }

  const checkValidationHandler = () => {
    if(formData.fname?.trim() === ''){
      setFormError((prevState) => ({...prevState, fnameError: 'First name can\'t be empty'})) 
    }
    if(formData.lname?.trim() === ''){
      setFormError((prevState) => ({...prevState, lnameError: 'Last name can\'t be empty'})) 
    }
    if(!formData.email?.includes('@')){
      setFormError((prevState)=> ({...prevState, emailError: "Email is not valid"}))
    }
  }

  return (
    <form onSubmit={formSubmitHandler}>
      <div className='control-group'>
        <div className='form-control'>
          <label htmlFor='name'>First Name</label>
          <input type='text' 
          id='name' 
          name = "fname" 
          onChange={formChangeHandler} 
          onBlur = {formTouchedChangeHandler}
          />

        </div>
        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' name= "lname" onChange={chageFormDateHandler}  onBlur = {checkValidationHandler}/>
          {formError.lnameError && <p className='error-text'>{formError.lnameError}</p>}
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' name= "email" onChange={chageFormDateHandler}  onBlur = {checkValidationHandler}/>
        {formError.emailError && <p className='error-text'>{formError.emailError}</p>}
      </div>
      <div className='form-actions'>
        <button type="submit">Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
